import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function TaskEditor() {
    const navigate = useNavigate();
    const [taskData, setTaskData] = useState({
        title: "",
        description: "",
        status: "To Do", // Default state
        due_date: "", // Should be in YYYY-MM-DD format
    });
    const [message, setMessage] = useState("");
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Get token from localStorage
        const token = localStorage.getItem('authToken');

        try {
            const response = await fetch("http://localhost:3000/api/v1/tasks", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`,  // Add token to the Authorization header
                },
                body: JSON.stringify({ task: taskData }),
            });

            const data = await response.json();

            if (response.ok) {
                setMessage(data.message);
                // Navigate to add subtask page with the task ID
                const taskId = data.task.id;
                setTimeout(() => navigate(`/tasks/${taskId}/subtasks`), 2000);
            } else {
                setMessage(data.message || "Error creating task");
            }
        } catch (error) {
            setMessage("Network error");
        }
    };

    const handleAddSubtaskClick = () => {
        // Simulate task creation ID for demonstration
        const taskId = 1; // Replace with actual ID logic
        navigate(`/tasks/${taskId}/subtasks`);
    };

    return (
        <div>
            <h2>Create Task</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Title:</label>
                    <input
                        type="text"
                        placeholder="Task Title"
                        value={taskData.title}
                        onChange={(e) => setTaskData({ ...taskData, title: e.target.value })}
                    />
                </div>
                <div>
                    <label>Description:</label>
                    <textarea
                        placeholder="Task Description"
                        value={taskData.description}
                        onChange={(e) =>
                            setTaskData({ ...taskData, description: e.target.value })
                        }
                    />
                </div>
                <div>
                    <label>Status:</label>
                    <select
                        value={taskData.status}
                        onChange={(e) => setTaskData({ ...taskData, status: e.target.value })}
                    >
                        <option value="To Do">To Do</option>
                        <option value="In Progress">In Progress</option>
                        <option value="Completed">Completed</option>
                    </select>
                </div>
                <div>
                    <label>Due Date:</label>
                    <input
                        type="date"
                        value={taskData.due_date}
                        onChange={(e) => setTaskData({ ...taskData, due_date: e.target.value })}
                    />
                </div>
                <button type="submit">Save Task</button>
            </form>
            <button onClick={handleAddSubtaskClick}>Add Subtask</button>
            {message && <p>{message}</p>}
        </div>
    );
}

export default TaskEditor;
